个人配置
user root owner;
worker_processes  1;
events {
  worker_connections  1024;
}


http {
  include       mime.types;
  default_type  application/octet-stream;
  sendfile        on;
  keepalive_timeout  65;

  # 本地
  server {
    listen       8080;
    server_name  localhost;
    location / {
        root   html;
        index  index.html index.htm;
    }
    error_page   500 502 503 504  /50x.html;
    location = /50x.html {
        root   html;
    }
  }

  # sassOperate（售后服务系统---运营端）
  server {
    listen       80;
    server_name  service.d.renrenauto.com;
    location / {
      proxy_pass http://10.4.82.23;
      proxy_set_header Host $host;
    }
    location ^~ /pages {
       root /Users/wangzhihui/Documents/rr-works/sassOperate-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # sassService（售后）
  server {
    listen       80;
    server_name  service.renrenauto.com;
    location / {
      proxy_pass http://10.4.82.23;
      proxy_set_header Host $host;
    }
    location ^~ /pages {
       root /Users/wangzhihui/Documents/rr-works/sassService-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # sassDealer（车商端）
  server {
    listen       80;
    server_name  admin.renrenauto.com;
    location ^~ /pages {
       root /Users/wangzhihui/Documents/rr-works/sassDealer-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # sassRun（运营端）
  server {
    listen       80;
    server_name  platform.d.renrenauto.com;
    location ^~ /view/finance {
       root /Users/wangzhihui/Documents/rr-works/finance-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # BDA（大数据分析）
  server {
    listen       80;
    server_name  bda.renrenauto.com;
    location ^~ /views {
       root /Users/wangzhihui/Documents/rr-works/bda-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # official（官网）
  server {
    listen       80;
    server_name  www.kaixin.com;
    location / {
      proxy_pass http://10.4.82.55;
      proxy_set_header Host $host;
    }
    location ^~ /html {
       root /Users/wangzhihui/Documents/rr-works/official-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # 静态文件
  server {
    listen       80;
    server_name  s.xnimg.cn a.xnimg.cn xnimg.cn;
    root    /Users/wangzhihui/Documents/rr-works/frontend/xn.static;
    autoindex       on;
    autoindex_exact_size    on;

    rewrite ^/a?([0-9])+/(.*)$ /$2 last;
    rewrite (.*)\.[0-9]+\.css /$1.css;
    rewrite (.*)\.[0-9]+\.js /$1.js;

    location / {
      index   index.html index.htm;
    }

    location ~* \.(?:ttf|ttc|otf|eot|woff)$ {
      add_header "Access-Control-Allow-Origin" "*";
      expires 1M;
      access_log off;
      add_header Cache-Control "public";
    }
  }

}

chapters/nginx/配置示例.html.body:
配置示例
后端配置示例
 server {
    listen       80;
    server_name  platform.d.renrenauto.com;

    error_page   500 502 503 504  /50x.html;
    location = /50x.html {
        root   html;
    }

    location / {
            proxy_pass http://127.0.0.1:8090;
            proxy_set_header   Host             $host;
            proxy_set_header   X-Real-IP        $remote_addr;
            proxy_set_header  X-Forwarded-For  $proxy_add_x_forwarded_for;
            client_max_body_size       10m;
            client_body_buffer_size    128k;
            proxy_connect_timeout      90;
            proxy_send_timeout         90;
            proxy_read_timeout         90;
            proxy_buffer_size          4k;
            proxy_buffers              4 32k;
            proxy_busy_buffers_size    64k;
            proxy_temp_file_write_size 64k;
    }
    location ~ /view {
            proxy_pass http://10.2.52.141;
            proxy_set_header   Host             $host;
            proxy_set_header   X-Real-IP        $remote_addr;
            proxy_set_header  X-Forwarded-For  $proxy_add_x_forwarded_for;
            client_max_body_size       10m;
            client_body_buffer_size    128k;
            proxy_connect_timeout      90;
            proxy_send_timeout         90;
            proxy_read_timeout         90;
            proxy_buffer_size          4k;
            proxy_buffers              4 32k;
            proxy_busy_buffers_size    64k;
            proxy_temp_file_write_size 64k;
    }
}

