并行执行
//延时n毫秒
function timeout(ms) {
  return new Promise((resolve) => {
    setTimeout(resolve, ms);
  });
}
function consoleNum() {
  let nums = [1, 2, 3, 4, 5, 6, 7, 8, 9];
