var Cookie={
    //获取cookie
    get:function(name){
        var cookieName=encodeURIComponent(name)+"=";//传入的参数编码后加上“=”
        var cookieStart=document.cookie.indexOf(cookieName);//找到传入参数在cookie中对应的位置
