    listen       80;
    server_name  platform.d.renrenauto.com;
    location ^~ /view/finance {
       root /Users/wangzhihui/Documents/rr-works/finance-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # BDA（大数据分析）
  server {
    listen       80;
    server_name  bda.renrenauto.com;
    location ^~ /views {
       root /Users/wangzhihui/Documents/rr-works/bda-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # official（官网）
  server {
    listen       80;
    server_name  www.kaixin.com;
    location / {
      proxy_pass http://10.4.82.55;
      proxy_set_header Host $host;
    }
    location ^~ /html {
       root /Users/wangzhihui/Documents/rr-works/official-project;
       index index.html index.htm;
    }
    autoindex       on;
    autoindex_exact_size    on;
  }

  # 静态文件
  server {
    listen       80;
    server_name  s.xnimg.cn a.xnimg.cn xnimg.cn;
    root    /Users/wangzhihui/Documents/rr-works/frontend/xn.static;
    autoindex       on;
    autoindex_exact_size    on;

    rewrite ^/a?([0-9])+/(.*)$ /$2 last;
    rewrite (.*)\.[0-9]+\.css /$1.css;
    rewrite (.*)\.[0-9]+\.js /$1.js;

    location / {
      index   index.html index.htm;
    }

    location ~* \.(?:ttf|ttc|otf|eot|woff)$ {
      add_header "Access-Control-Allow-Origin" "*";
      expires 1M;
      access_log off;
      add_header Cache-Control "public";
    }
  }

}

